# Describe this function...
def moveDown():
  global cowPosition, carY, carLane, score, carX, playing, carVelocity
  if cowPosition > 20:
    cowPosition = cowPosition - 40

# Describe this function...
def moveUp():
  global cowPosition, carY, carLane, score, carX, playing, carVelocity
  if cowPosition < 80:
    cowPosition = cowPosition + 40

# Describe this function...
def spawnCar():
  global cowPosition, carY, carLane, score, carX, playing, carVelocity
  if score == 0:
    carY = 50
  else:
    carLane = random.randint(1, 3)
    if carLane == 1:
      carY = 90
    elif carLane == 2:
      carY = 50
    else:
      carY = 10

# Describe this function...
def draw():
  global cowPosition, carY, carLane, score, carX, playing, carVelocity
  display.fill(Display.Color.Gray)
  display.blit(sprite_cow, int(15), int(cowPosition), sprite_cow_transparent)
  display.blit(sprite_car6, int(carX), int(carY), sprite_car6_transparent)
  carX = carX - carVelocity

# Describe this function...
def checkCollision():
  global cowPosition, carY, carLane, score, carX, playing, carVelocity
  if carY == cowPosition and carX <= 10:
    display.commit()
    display.blit(sprite_steak, int(10), int(cowPosition - 5), sprite_steak_transparent)
    display.commit()
    playing = False
  else:
    score = score + 1

# Describe this function...
def play():
  global cowPosition, carY, carLane, score, carX, playing, carVelocity
  checkCollision()
  if carX < -60:
    carX = 150
    spawnCar()
    if carVelocity < 45:
      carVelocity = carVelocity + 1
  buttons.scan()
  draw()
  checkCollision()
  display.text(str(score), int(10), int(10), 0)
  display.commit()


